
            var shiftKeyDownCount = 0;
            var ctrlKeyDownCount = 0;

            window.onkeydown = function (e) {
                if (!e)
                    e = window.event;
                if (e.shiftKey) {
                    shiftKeyDownCount++;
                    return false;
                }
                if (e.ctrlKey) {
                    ctrlKeyDownCount++;
                    return false;
                }
            }

            window.onkeyup = function (e) {
                if (!e)
                    e = window.event;
                shiftKeyDownCount = 0;
                ctrlKeyDownCount = 0;
            }

            function verify() {
                if (shiftKeyDownCount & ctrlKeyDownCount)
                    window.location =  getPage(); // need to change for redirecting to the login page
            }

